"use strict";

// * плавный скролл на якоря
$("body").on("click", '[href*="#"]', function (e) {
  var fixed_offset = 100;
  if (!$(this.hash).length) return;
  $("html,body").stop().animate({
    scrollTop: $(this.hash).offset().top - fixed_offset
  }, 2000);
  e.preventDefault();
});
/*phone mask */

$("[data-tel-input]").inputmask({
  mask: "+7 (999) 999-99-99",
  showMaskOnHover: false,
  placeholder: "_"
});
var tab = $(".tabs__btn");
var initialTitle = $(".hero h1").attr("data-initialTitle");

if ($(window).width() > 992) {
  tab.on("mouseenter", function () {
    var parent = $(this).closest(".tabs__item");
    var title = parent.attr("data-title");
    var bgImg = parent.attr("data-bg");
    parent.addClass("active").siblings().removeClass("active");
    $(".hero h1").find("strong").text(title);
    $(".hero").addClass("active").find(".hero__bg").attr("style", "background-image: url(".concat(bgImg, ")"));
    $(".header").addClass("white");
  });
  tab.on("mouseleave", function () {
    var parent = $(this).closest(".tabs__item");
    parent.removeClass("active");
    $(".hero").removeClass("active").find(".hero__bg").removeAttr("style");
    $(".hero h1").find("strong").html("".concat(initialTitle));
    $(".header").removeClass("white");
  });
} else {
  tab.on("click", function () {
    var parent = $(this).closest(".tabs__item");
    var title = parent.attr("data-title");
    var bgImg = parent.attr("data-bg");
    console.log(title);
    parent.toggleClass("active").siblings().removeClass("active");
    $(".hero h1").find("strong").text(title);
    $(".hero__bottom").attr("style", "background-image: url(".concat(bgImg, ")"));

    if ($(".hero").hasClass("active")) {
      $(".header").addClass("white");
    } else {
      $(".header").removeClass("white");
    }

    if ($(".tabs__item.active").length == 0) {
      $(".hero").removeClass("active");
      $(".header").removeClass("white");
      $(".hero h1").find("strong").html("".concat(initialTitle));
      $(".hero__bottom").removeAttr("style");
    }
  });
}

$(document).on("change", ".custom-input input", function (e) {
  if ($(this).val().trim()) {
    $(this).addClass("filled");
  } else {
    $(this).removeClass("filled");
    $(this).val("");
  }
}); // * Убирает прокрутку у body

$(".burger").on("click", function () {
  $(this).toggleClass("active");
  $(".mobile-menu").toggleClass("show");
  $("body").toggleClass("overflow");
});
